mapper.update:
update users
set status = #{status}
where user_id = #{user_id}

mapper.select:
SELECT *
FROM users
WHERE USER_ID = #{user_id}
AND PASSWORD = #{password}

SELECT *
FROM users
WHERE USER_ID = #{user_id}
AND PASSWORD = #{password}

SELECT count(*)
FROM users
WHERE USER_ID = #{user_id}
AND PASSWORD = #{password}

